import React, { Component } from "react";
import ReactDOM from "react-dom";
// import './index.css'
const library =
  "https://cdnjs.cloudflare.com/ajax/libs/marked/2.0.1/marked.min.js";
var newVar;

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      preview: placeholder,
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    this.setState({
      preview: e.target.value,
    });
  }
  convert() {
    let newVar = marked(this.state.preview);
    return { __html: newVar };
  }
  render() {
    let newVar = this.state.preview;

    return (
      <div>
        <div>
          <h2 id="welcome">Welcome to the Markdown Preview Page </h2>
          <h4 id="description">
            This is where you can test and preview your makdown drafts and see
            how they look in real time. Below you'll find a text area containing
            some pre-popoulate text intended to illustrate the different
            features and syntax of the Markdown language.
            <br />
            <br />
            Feel free to edit the text as you see fit. Happy previewing!
            <br />
          </h4>
        </div>
        <div id="boxes">
          <div id="onLoad">
            <h4 class="titles" id="textbox">Add/Edit text here: </h4>
            <h4 class="titles" id="preHeading">Preview: </h4>
            <textarea
              value={this.state.preview}
              id="editor"
              onChange={this.handleChange}
            />
          </div>
          <div id="mdpreview">
            <div
              id="preview"
              value={this.props.outputValue}
              dangerouslySetInnerHTML={this.convert()}
            >
              {this.state.outputValue}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
