import React, {Component} from 'react';
import ReactDOM from 'react-dom';

const library = "https://cdnjs.cloudflare.com/ajax/libs/marked/2.0.1/marked.min.js"
var newVar;

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      preview: placeholder
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    this.setState({
      preview: e.target.value
    });
  }
  convert() {
    let newVar = marked(this.state.preview);
    return { __html: newVar };
  }
  render() {
    let newVar = this.state.preview;

    return (
      <div id="boxes">
        <div id="onLoad">
          <h4>Input: </h4>
          <textarea
            value={this.state.preview}
            id="editor"
            onChange={this.handleChange}
          />
        </div>
        <div id="mdpreview">
          <h4>Output: </h4>

          <div
            id="preview"
            value={this.props.outputValue}
            dangerouslySetInnerHTML={this.convert()}
          >
            {this.state.outputValue}
          </div>
        </div>
      </div>
    );
  }
}
var placeholder = `# This is a Markdown Previewer. 
# This is an h1
## This is an h2
### This is an h3 
etc.
---
\`inline code is written betwteen 2 backticks\`
\`\`\` {
    multi-lined code is encased in braces and surrounded by backticks- 3 at the beginning & 3 at the end
}
\`\`\`
---
Links have [square brackets] around the title of the link, and (parenthesis) around the link address
[addressText](https://www.hello.com) 
---
Blockquotes are preceded by >'greater-than' signs. If the block is more than one line or paragraph, '>' goes in front of ever line, inlcuding the spaces and paragraph breaks.
> This is the first line, 
> 
> And this is what multi-lined looks like.
---
Lists come in 2 varieties: 
Ordered: where every entry is preceded by a number with a period:

1. Item one
2. Item two
3. Item three

Unordered: where each entry is preceded by one of multiple symbols that ultimately produces a bulleted list. 

- Item -one
- Item -two
|| or
* Item *one
* Item *two
|| or
+ Item +one
+ Item +two
---
The major text emphases are as follows:
* Bold: This is *** ***bold text*** ***
* Italic: This is * *Italics* * or this is _ _italics_ _
---

Images: To make an entry an image, add an exclamation mark followed by the entire text or descriptiion encased in square brackets, and the URL in parenthesis.

![Waterlillies. What a beautiful painting.](www.monetpic.jpg "Waterllies")
`;
ReactDOM.render(<App />, document.getElementById("root"));

export default App;




































